import java.util.ArrayList;public class Hive {    private final ArrayList<Wasp> listOfWasps;    public Hive() {        this.listOfWasps = new ArrayList<>();    }    public ArrayList<Wasp> getListOfWasps() {        return listOfWasps;    }    public int getHiveCount () {        return listOfWasps.size();    }    public void removeWaspFromHive(Wasp targetWasp){        listOfWasps.removeIf(wasp -> wasp.getId() == targetWasp.getId());    }}